INSERT INTO `SERVMASK_PREFIX_posts` VALUES (2,1,'2025-01-11 09:31:24','2025-01-11 09:31:24','<!-- wp:acf/block-banner {\"name\":\"acf/block-banner\",\"data\":{\"title\":\"Equicore\",\"_title\":\"field_678a5c5bd590c\",\"subtitle\":\"Кінно-спортивний комплекс\",\"_subtitle\":\"field_678a5c6dd590d\",\"image\":17,\"_image\":\"field_678a5c96e5924\"},\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/block-side-img {\"name\":\"acf/block-side-img\",\"data\":{\"is_revert\":\"1\",\"_is_revert\":\"field_6793a8797542f\",\"text\":\"Eqicore — це місце, де поєднуються традиції кінного спорту і сучасні методи тренувань, створюючи комфортну та безпечну атмосферу для коней та їхніх власників. Ми раді запропонувати вам професійне навчання верхової їзди, тренування для коней, а також комфортне розміщення для вашого улюбленця. Наш центр розташований у мальовничому куточку Львова, де природа та високий рівень обслуговування створюють ідеальні умови для розвитку кінних дисциплін.\",\"_text\":\"field_6793a09d9b153\",\"button\":{\"title\":\"Про нас\",\"url\":\"/pro-nas/\",\"target\":\"\"},\"_button\":\"field_6793a7bb82321\",\"image\":27,\"_image\":\"field_6793a090b1eb5\",\"disabled\":\"0\",\"_disabled\":\"field_678bb94aa4da7\"},\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/block-services {\"name\":\"acf/block-services\",\"data\":{\"image\":\"\",\"_image\":\"field_678bbdcddad7a\",\"services\":[\"26\"],\"_services\":\"field_678bc0e305962\",\"disabled\":\"0\",\"_disabled\":\"field_678bb94aa4da7\"},\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/block-team-slider {\"name\":\"acf/block-team-slider\",\"data\":{\"title\":\"НАША КОМАНДА\",\"_title\":\"field_678f590cdc819\",\"description\":\"Команда EquiCore складається з професіоналів, які мають великий досвід у кінному спорті та займаються розвитком цього виду спорту в Україні. Кожен член команди володіє унікальними навичками та знаннями, що дозволяє центру надавати високоякісні послуги з тренувань та обслуговування коней.\",\"_description\":\"field_678f585722c1a\",\"button\":{\"title\":\"наша команда\",\"url\":\"/\",\"target\":\"\"},\"_button\":\"field_678f58a622c1b\",\"team\":\"\",\"_team\":\"field_678f583530c14\",\"posts_per_page\":\"\",\"_posts_per_page\":\"field_678f583530ffd\",\"disabled\":\"0\",\"_disabled\":\"field_678bb94aa4da7\"},\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/block-faq {\"name\":\"acf/block-faq\",\"data\":{\"title\":\"Часті запитання\",\"_title\":\"field_678e2bba4665b\",\"faqs\":\"\",\"_faqs\":\"field_678e2bcf4665c\",\"img\":17,\"_img\":\"field_678e2c5e75cb6\",\"disabled\":\"0\",\"_disabled\":\"field_678bb94aa4da7\"},\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/block-gallery {\"name\":\"acf/block-gallery\",\"data\":{\"gallery_type\":\"0\",\"_gallery_type\":\"field_678e35a4af211\",\"galleries\":\"\",\"_galleries\":\"field_678e317971e17\",\"posts_per_page\":\"3\",\"_posts_per_page\":\"field_678e4f1c8c7b6\",\"disabled\":\"0\",\"_disabled\":\"field_678bb94aa4da7\"},\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/block-cta {\"name\":\"acf/block-cta\",\"data\":{\"title\":\"зв’яжіться з нами\",\"_title\":\"field_678fa50da0664\",\"image\":17,\"_image\":\"field_678fa7aa30f9f\",\"disabled\":\"0\",\"_disabled\":\"field_678bb94aa4da7\"},\"mode\":\"preview\"} /-->','Головна','','publish','closed','closed','','holovna','','','2025-01-29 16:11:24','2025-01-29 16:11:24','',0,'http://equicore/?page_id=2',0,'page','',0);
INSERT INTO `SERVMASK_PREFIX_posts` VALUES (3,1,'2025-01-11 09:31:24','2025-01-11 09:31:24','<!-- wp:acf/block-text-content {\"name\":\"acf/block-text-content\",\"data\":{\"text\":\"\\u003ch4\\u003e1. Політика сайту\\u003c/h4\\u003e\\r\\nВикладені нижче умови є обов’язковими умовами використання веб-сайту «Gift2Go» і послуг,\\r\\nщо надаються на сайті. Перегляд веб-сайту та доступ до продуктів і послуг регулюються\\r\\nумовами, викладеними в них\\r\\n\\u003cul\\u003e\\r\\n \\t\\u003cli\\u003eПоложення цих Умов застосовуються до будь-якого використання Сайту вами\\u003c/li\\u003e\\r\\n \\t\\u003cli\\u003eІ стане юридичною основою для будь-яких дискусій між вами та адміністратором сайту, як\\r\\nвизначено нижче. У зв’язку з цим просимо вас уважно та повністю прочитати ці Умови\\u003c/li\\u003e\\r\\n \\t\\u003cli\\u003eСкрізь, де в цих правилах вживаються займенники чоловічого роду, посилання також\\r\\nімпліцитно стосується жіночого роду.\\u003c/li\\u003e\\r\\n\\u003c/ul\\u003e\\r\\n\\u003ch4\\u003e2. Визначення\\u003c/h4\\u003e\\r\\nВаучер \\u0022: для цілей цього документа - його можна поширювати та реалізовувати в текстовому\\r\\nповідомленні, електронною поштою на смартфоні або роздрукувати та створювати за допомогою\\r\\nплатформи, якою керує веб-майстер\\r\\n\\u003col\\u003e\\r\\n \\t\\u003cli\\u003e«Подарунковий ваучер»: ваучер, який дає покупцеві право на отримання\\u003c/li\\u003e\\r\\n \\t\\u003cli\\u003eТовар або послуга взамін, за винятком грошової вартості та відповідно до положень цих\\r\\nправил\\u003c/li\\u003e\\r\\n \\t\\u003cli\\u003e«Грошовий ваучер»: ваучер, який дає право покупцеві отримати взамін\\u003c/li\\u003e\\r\\n \\t\\u003cli\\u003eГрошова вартість, яка в ньому стягується, і відповідно до положень цих Умов\\u003c/li\\u003e\\r\\n\\u003c/ol\\u003e\\r\\n\\u003ch4\\u003e3. Загальні\\u003c/h4\\u003e\\r\\nСайт розроблений, щоб дозволити клієнтам отримувати інформацію, переглядати, перевіряти та\\r\\nкупувати ваучери від різних постачальників, які час від часу змінюються, у простий та\\r\\nзручний спосіб. Користувач має право використовувати веб-сайт за умови виконання всіх\\r\\nнаступних умов.\\r\\n\\r\\nвеб-майстер залишає за собою право час від часу оновлювати ці умови на власний розсуд і\\r\\nбез необхідності будь-якого попереднього повідомлення, без необхідності попереднього\\r\\nповідомлення чи отримання згоди користувача на сайті, користувач несе відповідальність за\\r\\nознайомлення з умовами під час кожного відвідування сайту. Незважаючи на вищезазначене,\\r\\nвеб-майстер докладатиме максимум зусиль, щоб інформувати своїх користувачів за допомогою\\r\\nреклами на сайті про зміни, які стосуватимуться цих умов використання\",\"_text\":\"field_6793a311af7b1\",\"disabled\":\"0\",\"_disabled\":\"field_678bb94aa4da7\"},\"mode\":\"preview\"} /-->','Privacy Policy','','publish','closed','closed','','privacy-policy','','','2025-01-24 14:30:49','2025-01-24 14:30:49','',0,'http://equicore/?page_id=3',0,'page','',0);
